<template>
  <div class="media_window_container active" v-on:touchstart="dragStart" v-on:touchend="dragEnd" v-on:touchmove="drag" v-on:mousedown="dragStart" v-on:mouseup="dragEnd" v-on:mousemove="drag">
    <div class="media_window">
      <svg class="media_window_frame" data-name="01_Livestream" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="985" height="616" viewBox="0 0 985 616">
        <image id="bkgrd_credits" width="985" height="616" xlink:href="data:img/png;base64,"/>
        <image x="7" y="62" width="967" height="542" xlink:href="data:img/png;base64,"/>
      </svg>
      <VideoTitleBlock />
      <CloseBtn />
      <Video />
    </div>
  </div>
</template>

<script>
  import VideoTitleBlock from './VideoTitleBlock.vue';
  import CloseBtn from './CloseBtn.vue';
  import Video from './Video.vue';
  
  window.active = false;
  window.currentX = '';
  window.currentY = '';
  window.initialX = '';
  window.initialY = '';
  window.xOffset = 0;
  window.yOffset = 0;

  export default {
    name: 'MediaWindow',
    props: {
      msg: String
    },
    components: {
      VideoTitleBlock,
      CloseBtn,
      Video
    },
    methods: {
      dragStart: function(e) {
        if (e.type === "touchstart") {
          window.initialX = e.touches[0].clientX - window.xOffset;
          window.initialY = e.touches[0].clientY - window.yOffset;
        } else {
          window.initialX = e.clientX - window.xOffset;
          window.initialY = e.clientY - window.yOffset;
        }
        if (e.target.parentNode.parentNode === this.$el.children[0]) {
          window.active = true;
        }
      },

      dragEnd: function() {
        window.initialX = window.currentX;
        window.initialY = window.currentY;
        window.active = false;
      },

      drag: function(e) {
        if (window.active) {
          e.preventDefault();
          if (e.type === "touchmove") {
            window.currentX = e.touches[0].clientX - window.initialX;
            window.currentY = e.touches[0].clientY - window.initialY;
          } else {
            window.currentX = e.clientX - window.initialX;
            window.currentY = e.clientY - window.initialY;
          }
          window.xOffset = window.currentX;
          window.yOffset = window.currentY;
          this.$el.children[0].style.transform = "translate3d(" + window.currentX + "px, " + window.currentY + "px, 0)";
        }
      },
    }
  }
</script>

<style scoped>
  .media_window_container {
    align-items: center;
    background-color: transparent;
    bottom: 0;
    display: flex;
    height: 95vh;
    justify-content: center;
    left: 0;
    margin: 0 auto;
    max-width: 1700px;
    overflow: hidden;
    position: absolute;
    right: 0;
    top: 0;
    touch-action: none;
    width: 100vw;
  }
  .media_window {
    cursor: grab;
    height: 32.8rem;
    position: absolute;
    touch-action: none;
    user-select: none;
    width: 52rem;
    z-index: 2;
  }
  .media_window:active {
    cursor: grabbing;
  }
  .media_window_container.active {
    display: flex;
  }
  .media_window_container:not(.active) {
    display: none;
  }
  @media screen and (min-height: 950px) {
    .media_window_container {
      height: 97vh;
    }
  }
</style>